const initialState = {
    items: [],
};

function rootReducer(state = initialState, action) {
    switch (action.type) {
        case 'ADD_ITEM':
            return { ...state, items: [...state.items, action.payload] };
        case 'REMOVE_ITEM':
            return {
                ...state,
                items: state.items.filter((_, i) => i !== action.payload),
            };
        default:
            return state;
    };
};

export default rootReducer;